package com.zhipu.chinavideo.player;

import android.util.Log;

/**
 * @author: zhongxf
 * @Description: 当播放器的父类容器不是RelativeLayout
 * @date: 2016年4月27日
 */
public class NotRelativeLayoutException extends Exception {

	private static final long serialVersionUID = 1L;
	/**
	 *@author： zhongxf
	 *@Description: 父类容器不是RelativeLayout的打印
	 */
	@Override
	public void printStackTrace() {
		// TODO Auto-generated method stub
		super.printStackTrace();
		Log.e("ChinaVideo", "The ViewGroup of  VideoView is not RelativeLayout");
	}

}
